from typing import Optional

def binary_searh(arr, target) -> Optional[int]: #Return int or None

    if arr == None: return None

    left = 0
    right = len(arr)
    steps = 0

    while left < right:
        steps += 1
        mid = int((left + right) / 2)
        if target == arr[mid]:
            print(steps)
            return mid
        elif target > arr[mid]:
            left = mid + 1
        else:
            right = mid

    print(steps)
    return None    



